VALUES ('character deleted delete', 'Syntax: .character deleted delete #guid|$name\r\n\r\nCompletely deletes the selected characters.\r\nIf $name is supplied, only characters with that string in their name will be deleted, if #guid is supplied, only the character with that GUID will be deleted.', 4);
REPLACE INTO `command` (name, help, security)
VALUES ('character deleted list', 'Syntax: .character deleted list [#guid|$name]\r\n\r\nShows a list with all deleted characters.\r\nIf $name is supplied, only characters with that string in their name will be selected, if #guid is supplied, only the character with that GUID will be selected.', 3);
REPLACE INTO `command` (name, help, security)
VALUES ('character deleted old', 'Syntax: .character deleted old [#keepDays]\r\n\r\nCompletely deletes all characters with deleted time longer #keepDays. If #keepDays not provided the  used value from oregoncore.conf option ''CharDelete.KeepDays''. If referenced config option disabled (use 0 value) then command can''t be used without #keepDays.', 4);
REPLACE INTO `command` (name, help, security)
VALUES ('character deleted restore', 'Syntax: .character deleted restore #guid|$name [$newname] [#new account]\r\n\r\nRestores deleted characters.\r\nIf $name is supplied, only characters with that string in their name will be restored, if $guid is supplied, only the character with that GUID will be restored.\r\nIf $newname is set, the character will be restored with that name instead of the original one. If #newaccount is set, the character will be restored to specific account character list. This works only with one character!', 3);
REPLACE INTO `command` (name, help, security)
VALUES ('character erase', 'Syntax: .character erase $name\r\n\r\nDelete character $name. Character finally deleted in case any deleting options.', 4);
REPLACE INTO `command` (name, help, security)
VALUES ('modify fly', 'Syntax: .modify fly #rate\r\n.fly #rate\r\n\r\nModify the flying speed of the selected player to "normal base fly speed"*rate. If no player is selected, modify your fly.\r\n\r\n #rate may range from 0.1 to 50.', 1);

