VALUES (-1047009, 'Willix', 'I will rather rest a moment and come again to breath, before I return to Ratchet.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1047008, 'Willix', 'Finally! I am glad that I come, finally out here.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1047007, 'Willix', 'I am glad that we are out again from this damned ditch. However, up here it is not much better!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1047006, 'Willix', 'I think, I see a way how we come out of this damned thorn tangle.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1047005, 'Willix', 'I do not understand how these disgusting animals can live at such a place.... puh as this stinks!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1047004, 'Willix', 'Danger is behind every corner.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1047003, 'Willix', 'In this ditch there are Blueleaf Tuber! As if the gold waited only to be dug out, I say it you!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
