VALUES (-1047002, 'Willix', 'Help! Get this Raging Agam''ar from me!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1047001, 'Willix', 'There on top resides Charlga Razorflank. The damned old Crone.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1047000, 'Willix', 'Phew! Finally, out here. However, it will not become easy. Detain your eyes after annoyance.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1036003, 'smite SAY_PHASE_2', 'Now you''re making me angry!', '', '', '', '', '', '', '', '', 0, 0, 5779, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1036002, 'smite SAY_PHASE_1', 'You landlubbers are tougher than I thought, I''ll have to Improvise!', '', '', '', '', '', '', '', '', 0, 0, 5778, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1033009, '', 'Who dares interfere with the Sons of Arugal?', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1033008, 'prisoner ashcrombe SAY_BOSS_DIE_AS', 'For once I agree with you... scum.', '', '', '', '', '', '', '', '', 1, 7, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1033007, 'prisoner adamant SAY_BOSS_DIE_AD', 'About time someone killed the wretch.', '', '', '', '', '', '', '', '', 1, 1, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000735, 'geezle - EMOTE_SPARK', 'picks up the naga flag.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000734, 'geezle - GEEZLE_SAY_7', 'Yes, sir. It won''t happen again...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000733, 'geezle - SPARK_SAY_6', 'Our Bloodmyst scouts have located our contact. The fool, Velen, will soon leave himself open and defenseless -- long enough for us to strike! Now get out of my sight before I vaporize you...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000732, 'geezle - SPARK_SAY_5', 'Relax? Do you know what Kael''thas does to those that fail him, Geezle? Eternal suffering and pain... Do NOT screw this up, fool.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000731, 'geezle - GEEZLE_SAY_4', 'Relax, Spark! I have it all under control. We''ll strip mine the Exodar right out from under''em - making both you and I very, very rich in the process.', '', '', '', '', '', '', '', '', 4, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000730, 'geezle - SPARK_SAY_3', 'The Master has gone to great lengths to secure information about the whereabouts of the Exodar. You could have blown the entire operation, including the cover of our spy on the inside.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000729, 'geezle - SPARK_SAY_2', 'What''s the big idea? You nearly blew my cover, idiot! I told you to put the compass and navigation maps somewhere safe - not out in the open for any fool to discover.', '', '', '', '', '', '', '', '', 4, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000728, 'geezle - GEEZLE_SAY_1', 'What''s the big idea, Spark? Why''d you call for this meeting?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000727, 'Theramore Guard - SAY_QUEST9', 'What a bunch of lunatics! You actually believe this stuff?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000726, 'Theramore Guard - SAY_QUEST8', 'I''d never join anything like this. Better keep this, though. It''ll come in handy in the privy...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000725, 'Theramore Guard - SAY_QUEST7', 'This... this is a joke, right?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000724, 'Theramore Guard - SAY_QUEST6', '...as a deserter, you''ll enjoy activities like tethered swimming and dog grooming? How ridiculous!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000723, 'Theramore Guard - SAY_QUEST5', '...to establish the chicken as the official symbol of Theramore?! These guys are nuts!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000722, 'Theramore Guard - SAY_QUEST4', '...the deserters seek a Theramore where citizens will be free to juggle at all hours of the day? What is this nonsense?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000721, 'Theramore Guard - SAY_QUEST3', '...to establish a fund for the purchase of hair gel? I like my hair the way it is, thanks!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000720, 'Theramore Guard - SAY_QUEST2', '...receive 50 percent off deserter undergarments? What is this garbage?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000719, 'Theramore Guard - SAY_QUEST1', 'Hey, thanks.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000718, '', 'Thanks again. Sergeant Doryn will be glad to hear he has one less scout to replace this week.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000717, '', 'Let''s keep moving. I don''t like this place.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000716, '', 'Thanks for your help. Let''s get out of here!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000715, 'SAY_WP_6', 'Thank you my friend for making this possible. This is a day that I shall never forget! I think I will stay a while. Please return to High Priestess MacDonnell at the camp. I know that she''ll be keenly interested to know of what has transpired here.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000714, 'SAY_WP_5', 'Please, rise my friend. Keep the Blessing as a symbol of the strength of the Light and how heroes long gone might once again rise in each of us to inspire.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000713, 'SAY_WP_4', 'Thank you for coming here in remembrance of me. Your efforts in recovering that symbol, while unnecessary, are certainly touching to an old man''s heart.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000712, 'SAY_WP_3', 'Be welcome, friends!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000711, 'SAY_WP_2', 'This land truly needs to be cleansed by the Light! Let us continue on to the tomb. It isn''t far now...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000710, 'SAY_WP_1', 'It must be the purity of the Mark of the Lightbringer that is drawing forth the Scourge to attack us. We must proceed with caution lest we be overwhelmed!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000709, 'SAY_WP_0', 'Beware! We are attacked!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000708, 'REMULOS_SAY_10', 'Let us leave Nightaven, hero. Seek me aut at the grove.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000707, 'REMULOS_SAY_9', 'It will be done, Eranikus. Be well, ancient one.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000706, 'ERANIKUS_SAY_4', 'And heroes... I hold that which you seek. May it once more see the evil dissolved. Remulos, see to it that our champion receives the shard of the Green Flight.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000705, 'ERANIKUS_SAY_3', 'My lady, I am unworthy of your prayer. Truly, you are an angel of light. Please, assist me in returning to the barrow den so that I may return to the Dream. I like Malfurion, also have a love awaiting me... I must return to her... to protect her...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000704, 'ERANIKUS_SAY_2', ' But... Malfurion, Cenarius, Ysera... They still fight. They need me. I must return to the Dream at once.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000703, 'ERANIKUS_SAY_1', 'For so long, I was lost... The Nightmare''s corruption had consumed me... And now, you... all of you.. you have saved me. Released me from its grasp.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000702, 'TYRANDE_YELL_4', 'Praise be to Elune... Eranikus is redeemed.', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000701, 'TYRANDE_EMOTE_KNEE', 'falls to one knee.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000700, 'ERANIKUS_ZONE_YELL_CONSUMED', 'is wholly consumed by the Light of Elune. Tranquility sets in over the Moonglade.', '', '', '', '', '', '', '', '', 0, 0, 0, 6);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000699, 'ERANIKUS_YELL_10', 'I... I feel... I feel the touch of Elune upon my being once more... She smiles upon me... Yes... I...', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000698, 'ERANIKUS_YELL_9', 'WHY? Why did this happen to... to me? Where were you Tyrande? Where were you when I fell from the grace of Elune?', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000697, 'ERANIKUS_YELL_8', 'IT BURNS! THE PAIN.. SEARING...', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000696, 'TYRANDE_SAY_2', 'The grasp of the Old Gods is unmoving. He is consumed by their dark thoughts... I... I... I cannot... cannot channel much longer... Elune aide me.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000695, 'TYRANDE_YELL_3', 'You will be forgiven, Eranikus. Elune will always love you. Break free of the bonds that command you!', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000694, 'TYRANDE_YELL_2', 'Seek absolution, Eranikus. All will be forgiven...', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000693, 'TYRANDE_SAY_1', 'Tend to the injuries of the wounded, sisters!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000692, 'TYRANDE_YELL_1', 'Elune, hear my prayers. Grant us serenity! Watch over our fallen...', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000691, 'ERANIKUS_YELL_7', 'Remulos, look how easy they fall before me? You can stop this, fool. Turn the druid over to me and it will all be over...', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000690, 'ERANIKUS_YELL_6', 'Defeated my minions? Then face me, mortals!', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000689, 'ERANIKUS_YELL_5', 'Where is your savior? How long can you hold out against my attacks?', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000688, 'REMULOS_SAY_8', 'We will battle these fiends, together! Nighthaven''s Defenders are also among us. They will fight to the death if asked. Now, quickly, we must drive these aberations back to the Nightmare. Destroy them all!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000687, 'ERANIKUS_YELL_4', 'Rise, servants of the Nightmare! Rise and destroy this world! Let there be no survivors...', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000686, 'REMULOS_SAY_7', 'Please, champion, protect our people.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000685, 'REMULOS_SAY_6', 'Hurry, $N! We must find protective cover!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000684, 'ERANIKUS_EMOTE_ROAR', 'roars furiously.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000683, 'ERANIKUS_YELL_3', 'My redemption? You are bold, little one. My redemption comes by the will of my god.', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000682, 'REMULOS_YELL_2', 'Who is the predictable one, beast? Surely you did not think that we would summon you on top of Malfurion? Your redemption comes, Eranikus. You will be cleansed of this madness - this corruption.', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000681, 'ERANIKUS_YELL_2', 'You are certanly not your father, insect. Should it interest me, I would crush you with but a swipe of my claws. Turn Shan''do Stormrage over to me and your pitiful life will be spared along with the lives of your people.', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000680, 'ERANIKUS_EMOTE_LAUGHT', 'lets loose a sinister laugh.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000679, 'REMULOS_YELL_1', 'Fiend! Face the might of Cenarius!', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000678, 'ERANIKUS_YELL_1', 'Pitful predictable mortals... You know not what you have done! The master''s will fulfilled. The Moonglade shall be destroyed and Malfurion along with it!', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000677, 'ERANIKUS_ZONE_YELL_ENTER', 'Eranikus, Tyrant of the Dream, has entered our world.', '', '', '', '', '', '', '', '', 0, 0, 0, 6);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000676, 'REMULOS_SAY_5', 'Defend Nightaven, hero...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000675, 'REMULOS_SAY_4', 'He will stop at nothing to get to Malfurion''s physical manifistation. That must not happen... We must keep the beast occupied long enough for Tyrande to arrive.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000674, 'REMULOS_SAY_3', 'The rift will be opened there, above the Lake Elun''ara. Prepare yourself, $N. Eranikus''s entry into our world will be wrought with chaos and strife.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000673, 'REMULOS_SAY_2', 'To Nighthaven! Keep your army close, champion.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000672, 'REMULOS_SAY_1', 'We will locate the origin of the Nightmare through the fragments you collected, $N. From there, we will pull Eranikus through a rift in the Dream. Steel yourself, $C. We are inviting the embodiment of the Nightmare into our world.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000671, '', 'I shall use the time wisely, milord. Thank you.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000670, '', 'Ah, thank you kindly. I will leave you to the library while I tend to this small matter.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000669, '', 'Milord, your guest has arrived. He awaits your presence.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000668, '', 'Thank you. The Light be with you both.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000667, '', 'Of course. He awaits you in the library.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000666, '', 'Good day to you both. I would speak to Lord Lescovar.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000665, '', 'Wait here. Spybot will make Lescovar come out as soon as possible. Be ready! Attack only after you''ve overheard their conversation.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000664, '', 'By your command!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000663, '', 'The Defias shall succeed! No meek adventurer will stop us!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000662, '', 'That''s it! That''s what you were waiting for! KILL THEM!', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000661, '', 'Hmm, it could be that meddle Shaw. I will see what I can discover. Be off with you. I''ll contact you again soon.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000660, '', 'VanCleef sends word that the plans are underway. But he''s hear rumors about someone snooping about.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000659, '', 'There you are. What news from Westfall?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000658, '', 'Yes, sir!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000657, '', 'It''s time for my meditation, leave me.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000656, '', 'Twilight Corrupter squeezes the last bit of life out of $N and swallows their soul.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000655, '', 'The Nightmare cannot be stopped!', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000654, '', 'Come, $N. See what the Nightmare brings...', '', '', '', '', '', '', '', '', 0, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000653, 'MERITHRA_EMOTE_1', 'glances at her compatriots.', '', '', '', '', '', '', '', '', 2, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000652, 'MERITHRA_YELL_1', 'Succumb to the endless dream, little ones. Let it consume you!', '', '', '', '', '', '', '', '', 53, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000651, 'MERITHRA_SAY_2', 'We will push them back, Anachronos. This I vow. Uphold the end of this task. Let not your hands falter as you seal our fates behind the barrier.', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000650, 'MERITHRA_SAY_1', 'There is a way...', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000649, 'ARYGOS_EMOTE_1', 'nods knowingly.', '', '', '', '', '', '', '', '', 255, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000648, 'ARYGOS_YELL_1', 'Let them feel the wrath of the Blue Flight! May Malygos protect me!', '', '', '', '', '', '', '', '', 53, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000647, 'ARYGOS_SAY_1', 'This distraction will give you and the young druid time enough to seal the gate. Do not falter. Now, let us see how they deal with chaotic magic.', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000646, 'CAELESTRASZ_YELL_1', 'Alexstrasza grant me the resolve to drive our enemies back!', '', '', '', '', '', '', '', '', 53, 0, 0, 1);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000645, 'CAELESTRASZ_SAY_2', 'Do not forget the sacrifices made on this day, night elf. We have all suffered immensely at the hands of these beasts.', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000644, 'CAELESTRASZ_SAY_1', 'Aye, Fandral, remember these words: Let not your grief guide your faith. These thoughts you hold... dark places you go, night elf. Absolution cannot be had through misguided vengeance.', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000643, 'FANDRAL_EMOTE_2', 'hurls the Scepter of the Shifting Sands into the barrier, shattering it.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000642, 'FANDRAL_EMOTE_1', 'falls to one knee - exhausted.', '', '', '', '', '', '', '', '', 16, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000641, 'FANDRAL_SAY_6', 'My son''s soul will find no comfort in this hollow victory, dragon. I will have him back.Thought it takes a millennia, I WILL have my son back!', '', '', '', '', '', '', '', '', 254, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000640, 'FANDRAL_SAY_5', 'I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000639, 'FANDRAL_SAY_4', 'After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', '', '', '', '', '', '', '', '', 25, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000638, 'FANDRAL_SAY_3', 'Ancient ones guide my hand... Wake from your slumber! WAKE AND SEAL THIS CURSED PLACE!', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000637, 'FANDRAL_SAY_2', 'It is done dragon. Lead the way...', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000636, 'FANDRAL_SAY_1', 'My forces cannot overcome the Qiraji defenses. We will not be able to get close enough to place your precious barrier, dragon.', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000635, 'ANACHRONOS_EMOTE_3', 'kneels down to pick up the fragments of the shattered scepter.', '', '', '', '', '', '', '', '', 87, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000634, 'ANACHRONOS_EMOTE_2', 'shakes his head in dissapointment.', '', '', '', '', '', '', '', '', 255, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000633, 'ANACHRONOS_EMOTE_1', 'hands the Scepter of the Shifting Sands to Fandral Staghelm.', '', '', '', '', '', '', '', '', 1, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000632, 'ANACHRONOS_SAY_10', 'And now you know all that there is to know, mortal...', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000631, 'ANACHRONOS_SAY_9', 'Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000630, 'ANACHRONOS_SAY_8', 'Before I leave this place, I make one final offreing to you, Lord Staghelm. Should a time arise in which you must gain entry to this accursed fortress, use the Scepter of the Shifting Sands on the sacred gong. The magic holding the barrier together will dissipate and the horrors of Ahn''Qiraj will be unleashed upon the world once more.', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000629, 'ANACHRONOS_SAY_7', 'There is but one duty that remains...', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000628, 'ANACHRONOS_SAY_6', 'It... It is over, Lord Staghelm. We are victorious. Albeit the cost for this victory was great.', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000627, 'ANACHRONOS_SAY_5', 'FINISH THE SPELL STAGHELM! I CANNOT HOLD THE GLYPHS OF WARDING IN PLACE MUCH LONGER! CALL FORTH THE ROOTS!', '', '', '', '', '', '', '', '', 53, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000626, 'ANACHRONOS_SAY_4', 'The sands of time will halt, but only for a moment! I will now conjure the barrier.', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000625, 'ANACHRONOS_SAY_3', 'Stay close...', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000624, 'ANACHRONOS_SAY_2', 'NOW, STAGHELM! WE GO NOW! Prepare your magic!', '', '', '', '', '', '', '', '', 81, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000623, 'ANACHRONOS_SAY_1', 'We must act quickly or all shall be lost!', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000622, 'npc_lazy_peon - SAY_WP_0', 'Ow! OK, I''ll get back to work, $N!', '', '', '', '', '', '', '', '', 1, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000621, 'stinky - emote disapper', '%s disappears back into the swamp.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000620, 'stinky - aggro', 'Help! The beast is on me!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000619, 'stinky - quest complete', 'I can make it from here.  Thanks, $N!  And talk to my employer about a reward!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000618, 'stinky - say6', 'Ok, now let''s get out of here! ', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000617, 'stinky - say5', 'Come, $N!  Let''s go over there and collect it!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000616, 'stinky - say4', 'Ah, there''s one! ', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000615, 'stinky - say3', 'There must be one around here somewhere...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000614, 'stinky - say2', 'Nope, not here...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000613, 'stinky - say1', 'Now let''s look for the herb.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000612, 'stinky - quest accepted', 'Ok, let''s get started.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000611, 'melizza say SAY_POST_EVENT_3', 'Well, I''m off to the Gelkis. They''re not as dumb as the Maraudines, but they''re more reasonable.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000610, 'melizza say SAY_POST_EVENT_2', 'We''re going to have to scratch the Maraudines off our list. Too hard to work with...', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000609, 'melizza say SAY_POST_EVENT_1', 'Hey Hornizz! I''m back! And there are some people behind me who helped me out of a jam.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000608, 'melizza say SAY_COMPLETE', 'We made it! Thanks again! I''m going to run ahead!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000607, 'melizza say SAY_START', 'Thanks $N. Now let''s get out of here', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000531, 'letoll SAY_LE_THANKS', 'You''ve been a tremendous help, $r! Let''s get out of here before more of those things show up! I''ll let Dwarfowitz know you did the job asked of ya'' admirably.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000530, 'letoll EMOTE_LE_PICK_UP', '%s picks up the drum.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000529, 'letoll SAY_LE_HELP_HIM', 'Don''t just stand there! Help him out!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000528, 'letoll SAY_LE_IN_YOUR_FACE', 'IN YOUR FACE! I told you there was somethin'' here!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000527, 'letoll SAY_LE_REPLY_HEAR', 'Say, do you guys hear that?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000526, 'letoll SAY_LE_SHUT', 'Shut yer yaps! I''m gonna bang on this drum and that''s that!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000525, 'letoll SAY_LE_NO_LEAVE_REPLY4', 'I dunno. It can''t possibly beat the time he tried to convince us that we''re all actually a figment of some being''s imagination and that they only use us for their own personal amusement. That went over well during dinner with the family.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000524, 'letoll SAY_LE_NO_LEAVE_REPLY3', 'I don''t think it can top the one time where he told us that he''d heard that Artha''s "cousin''s" skeleton was frozen beneath a glacier in Winterspring. I''ll never forgive you for that one, Letoll. I mean honestly... Artha''s cousin?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000523, 'letoll SAY_LE_NO_LEAVE_REPLY2', 'Or that time when you told us that you''d discovered the cure for the plague of the 20th century. What is that even? 20th century?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000522, 'letoll SAY_LE_NO_LEAVE_REPLY1', 'This reminds me of that one time where you made us search Silithus for evidence of sand gnomes.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000521, 'letoll SAY_LE_NO_LEAVE', 'Have ye gone mad? You expect me to leave behind a drum without first beatin'' on it? Not this son of Ironforge! No sir!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000520, 'letoll SAY_LE_DISCOVERY_REPLY', 'Yea, great. Can we leave now? This desert is giving me hives.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000519, 'letoll SAY_LE_DISCOVERY', 'This discovery will surely rock the foundation of modern archaeology.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000518, 'letoll SAY_LE_DRUM_REPLY', 'Wow... a drum.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000517, 'letoll SAY_LE_DRUM', 'By brann''s brittle bananas! What is it!? It... It looks like a drum.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000516, 'letoll SAY_LE_ALMOST', 'Almost got it!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000515, 'letoll SAY_LE_BURIED', 'I think there''s somethin'' buried here, beneath the sand!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000514, 'letoll SAY_LE_ARRIVE', 'We''re here! Start diggin''!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000513, 'letoll SAY_LE_NORTH', 'The dig site is just north of here.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000512, 'letoll SAY_LE_KEEP_SAFE', 'Aright, $r, just keep us safe from harm while we work. We''ll pay you when we return.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000511, 'letoll SAY_LE_START', 'Aright, listen up! Form a circle around me and move out!', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000498, 'ancestral wolf SAY_WOLF_WELCOME', 'Welcome, kind spirit. What has brought you to us?', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000497, 'ancestral wolf EMOTE_WOLF_HOWL', '%s lets out a howl that rings across the mountains to the north and motions for you to follow.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000496, 'ancestral wolf EMOTE_WOLF_LIFT_HEAD', '%s lifts its head into the air, as if listening for something.', '', '', '', '', '', '', '', '', 0, 0, 0, 2);
REPLACE INTO `script_texts` (entry, comment, content_default, content_loc1, content_loc2, content_loc3, content_loc4, content_loc5, content_loc6, content_loc7, content_loc8, emote, language, sound, type)
VALUES (-1000488, 'maghar captive SAY_MAG_COMPLETE', 'It is best that we split up now, in case they send more after us. Hopefully one of us will make it back to Garrosh. Farewell stranger.', '', '', '', '', '', '', '', '', 0, 0, 0, 0);
